import React from 'react';


export default ({ data, page, onClick, text }) => {

    let list = [];

    for (let i = 0; i < data[page].ans.length; i++) {
        list.push(
            <li class="list-group-item"><input type="checkbox" />{data[page].ans[i][0]}</li>
        )
    }

    let html_ = page == 3 ? '' : list;
    let title_ = page == 3 ? '測試完成' : data[page].question;
    let text_ = page == 3 ? '結束' : text;


    return (
        <div>
            <h3>{title_}</h3>
            <ul class="list-group">
                {html_}
            </ul>

            <button class="btn btn-outline-secondary" type="button" onClick={onClick}>{text_}</button>
        </div>
    )
};
