import React, { Fragment, Component } from 'react';

class Lesson2 extends Component {

    state = {
        title: null,
        food: ["拉麵", "滷肉飯", "水餃"],
        imgs: [
            "https://uc.udn.com.tw/photo/2019/12/11/realtime/7183211.jpg",
            "https://d3l76hx23vw40a.cloudfront.net/recipe/yqn093-027c.jpg",
            "https://foodpicks.tw/wp-content/uploads/2019-11-03_114250-696x406.jpg"
        ]

    };


    foodClick = () => {
        let numberOfListItem = this.state.imgs.length;
        let randomChildNumber = Math.floor(Math.random() * numberOfListItem);
        this.setState(
            {
                title: randomChildNumber,
            }
        )
    };


    render() {
        return (
            <Fragment>
                <section id="lesson2">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 2</h2>
                                    <hr class="divider-title" />
                                </div>
                                
                                <p class="china-font">社團課程時間表</p>

                                <table class="table">
                                    <thead class="thead-dark">
                                        <tr>
                                            <th scope="col">#</th>
                                            <th scope="col">First</th>
                                            <th scope="col">Last</th>
                                            <th scope="col">Handle</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <th scope="row">1</th>
                                            <td>Mark</td>
                                            <td>Otto</td>
                                            <td>@mdo</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )

    }
}

export default Lesson2;
