import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson3 extends Component {


    state = {
        page: 0,
        btnText: '開始',
        data: [
            {
                question: "你現在對今年非常樂觀？",
                answers: [
                    ["是的", 2],
                    ["不是", 3],
                    ["不知道", 4]
                ]
            },
            {
                question: "你覺得自己是不是越來越不可靠了？",
                answers: [
                    ["是的", 3],
                    ["不是", 4],
                    ["一般", 5]
                ]
            },
            {
                question: "想到新的一年來到，就非常開心？",
                answers: [
                    ["是的", 4],
                    ["不是", 5],
                    ["還好", 6]
                ]
            },
            {
                question: "如果給你五百萬要你離開很愛的戀人，你會？",
                answers: [
                    ["好的", 5],
                    ["都可以", 6],
                    ["不要", 7]
                ]
            },
            {
                question: "過去一年裡，你有沒有受到很大的打擊？",
                answers: [
                    ["有的", 6],
                    ["沒有", 7],
                    ["還在承受著呢", 8]
                ]
            },
            {
                question: "你喜歡下面哪一個形容天氣的詞？",
                answers: [
                    ["風和日麗", 7],
                    ["豔陽高照", 8],
                    ["秋高氣爽", 6]
                ]
            },
            {
                question: "你覺得自己的愛情一直是比較不一般的？",
                answers: [
                    ["是的", 8],
                    ["不是", 9],
                    ["不知道", 10]
                ]
            }
        ],
        num: 0,
        count: 0,
        allSores: [
            "老虎",
            "孔雀",
            "無尾熊",
            "貓頭鷹",
            "變色龍"
        ]
    }


    showNext = (e) => {

        if (this.state.page > this.state.data.length) {
            this.setState({
                page: 0,
                btnText: '開始',
                num: 0,
                count: 0
            })
        } else {

            let number = this.state.page < this.state.data.length ? 1 + this.state.page : this.state.data.length + 1;

            let counts = parseInt(this.state.count);
                counts += parseInt(this.state.num);
            
            let allSores_ = '';

            if (counts > 30 && counts <= 35) {
                allSores_ = this.state.allSores[4]
            } else if (counts > 30 && counts < 36) {
                allSores_ = this.state.allSores[3]
            } else if (counts > 35 && counts < 41) {
                allSores_ = this.state.allSores[2]
            } else if (counts > 40 && counts < 51) {
                allSores_ = this.state.allSores[1]
            } else if (counts > 50 && counts < 56) {
                allSores_ = this.state.allSores[0]
            }

            let text_ = number < this.state.data.length ? '下一個' : '總分 : ' + this.state.count + ' 性格 : ' + allSores_;

            this.setState({
                page: number,
                btnText: text_,
                num: 0,
                count: counts
            })
        }
    }

    getTotal = (e) => {

        this.setState({ num: e.target.value })

        if (this.state.page == 2) {
            this.setState({ count: parseInt(this.state.count) + parseInt(e.target.value) })
        }

        if(this.state.num == 0)
        {
            var object = this.refs.l3_btn;  

            object.disabled = 'disabled';
        }

    }


    render() {
        return (
            <Fragment>
                <section id="lesson3">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 3</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">心理測試</p>
                                <p class="china-font">你的愛情是什麼滋味?</p>
                                <Controller data={this.state.data} page={this.state.page} onClick={this.showNext.bind(this)} text={this.state.btnText} count={this.state.count} onchange={this.getTotal.bind(this)} />
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )

    }
}

export default Lesson3;
