import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson4 extends Component {

    constructor(props) {
        super(props);

        this.talkBox = React.createRef();

        this.state = {
            imgMain: new Image(),
            mapArray: [0,1,1,0,0,0,3,1,2]
        }
    }

    componentDidMount() {
        this.updateCanvas();
    }

    updateCanvas(){
        let imgMain = new Image();

        // let ctx = this.talkBox.current;
        // ctx.getContext('2d');
        // console.log(ctx);
        
        // const ctx = this.refs.canvas.getContext('2d');
        const ctx = this.talkBox.getContext('2d');

        imgMain.src = "../assets/img/canvas/spriteSheet.png";



        imgMain.onload = function(){
            ctx.drawImage(imgMain,0,0,80,130,0,0,50,50);
        }

    }





    // play = () => {
    //     console.log(this.talkBox.current);
    // }


    render() {
        return (
            <Fragment>
                <section id="lesson4">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 4</h2>
                                    <hr class="divider-title" />
                                </div>
                                <p class="china-font">RPG</p>
                                <p class="china-font">九宮格</p>

                                <br/>
                                <canvas class="canvas" ref="canvas"></canvas>

                                <Controller talkBox={this.talkBox} />
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )
    }
}

export default Lesson4;
