import React from 'react';
import './card.css';
import optionsImg from './icon/option.svg';
// import clearDayImg from './icon/clear-day.svg';

export default ({str}) => {

    return (
        <div>
            <h1>{str}</h1>
            <img src={this.dinner} title={str}/>
        </div>
    )


    function dinner(data)
    {

        let images = [
            "https://uc.udn.com.tw/photo/2019/12/11/realtime/7183211.jpg",
            "https://d3l76hx23vw40a.cloudfront.net/recipe/yqn093-027c.jpg",
            "https://foodpicks.tw/wp-content/uploads/2019-11-03_114250-696x406.jpg"
        ];
        
        switch(data)
        {
            case "拉麵":
                return images[0];
                break;
            case "滷肉飯":
                return images[1];
                break;
            case "水餃":
                return images[2];
                break;
        }
    }
}
