import React from 'react';
import '../App.css';

export default ({talkBox}) => {


        let imgMain = new Image();
        const ctx = talkBox.getContext('2d');

        console.log(ctx);
        

        imgMain.src = "../assets/img/canvas/spriteSheet.png";



        imgMain.onload = function(){
            ctx.drawImage(imgMain,0,0,80,130,0,0,50,50);
        }

    

    return (
        <div>
            <canvas class="canvas" ref={talkBox}></canvas>
            <div class="talkBox"></div>
        </div>
    )
};
