import React from 'react';


export default ({ data, page, onClick, text, count, onchange }) => {

    let list = [];

    if(page < 3){
        for (let i = 0; i < data[page].ans.length; i++) {
            list.push(
                <li class="list-group-item">
    
                    <div class="form-check">
                        <input class="form-check-input" type="radio" name={data[page].question} id={data[page].question} value={data[page].ans[i][1]}  onChange={onchange} />
                        <label class="form-check-label" for={data[page].question}>
                            {data[page].ans[i][0]}
                        </label>
                    </div>
                </li>
            )
        }
    
    }


    let html_ = page == 3 ? '' : list;
    let title_ = page == 3 ? '測試完成' : data[page].question;
    let text_ = page == 3 ? '總分: '+ count : text;

    return (
        <div>
            <h3>{title_}</h3>

            <ul class="list-group">
                {html_}
            </ul>

            <button class="btn btn-outline-secondary" type="button" onClick={onClick}>{text_}</button>
        </div>
    )
};
