import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson4 extends Component {

    constructor(props) {
        super(props);

        this.talkBox = React.createRef();
        this.talkBox.canvas.getContext('2d');

        this.state = {
            currentImgX: 0,
            currentImgY: 0,
            imgMain: new Image(),
            mapArray: [0, 1, 1, 0, 0, 0, 3, 1, 2]
        }
    }

    componentDidMount() {
        this.updateCanvas();
    }

    componentDidUpdate(e) {
        console.log(e.code);

        this.updateCanvas();
    }


    componentWillReceiveProps({ keydown }) {
        if (keydown.event) {
            // inspect the keydown event and decide what to do
            console.log(keydown.event.which);
        }
    }

    updateCanvas() {
        const ctx = this.refs.canvas.getContext('2d');
        const imgMain = new Image();
        const imgMountain = new Image();
        const imgEnemy = new Image();
        const currentImgX = this.state.currentImgX;
        const currentImgY = this.state.currentImgY;
        imgMain.src = '../assets/img/canvas/spriteSheet.png';
        imgMountain.src = '../assets/img/canvas/material.png';
        imgEnemy.src = '../assets/img/canvas/Enemy.png';
        imgMain.onload = function () {
            ctx.drawImage(imgMain, 0, 0, 80, 130, currentImgX, currentImgY, 50, 50);
        }

        let arr = this.state.mapArray;

        imgMountain.onload = function () {
            imgEnemy.onload = function () {
                for (let x in arr) {
                    if (arr[x] == 1) {
                        ctx.drawImage(imgMountain, 32, 65, 32, 32, x % 3 * 150, Math.floor(x / 3) * 200, 50, 50);
                        ctx.drawImage(imgMountain, 32, 65, 32, 32, x % 3 * 200, Math.floor(x / 3) * 200, 50, 50);
                        ctx.drawImage(imgMountain, 32, 65, 32, 32, x % 3 * 250, Math.floor(x / 3) * 200, 50, 50);

                        ctx.drawImage(imgMountain, 32, 65, 32, 32, x % 3 * 150, 105, 50, 50);
                        // ctx.drawImage(imgMountain,32,65,32,32,x%3*200,Math.floor(x/3)*200,50,50);
                    }
                    else if (arr[x] == 3) {
                        ctx.drawImage(imgEnemy, 7, 40, 104, 135, x % 3 * 200, 100, 50, 50);
                    }
                }
            }
        }

        // ctx.clearRect(currentImgX,currentImgY,50,50);


        // ctx.drawImage(imgMain,0,0,80,130,100,100,50,50);
    }





    play = (e) => {
        console.log(e.target.textContent);
        
        let move = e.target.textContent;

        switch(move){
            case '上':
                break;
            case '下':
                break;
            case '左':
                break;
            case '右':
                break;
            default:
                // imgMain.onload = function () {
                //     ctx.drawImage(imgMain, 0, 0, 80, 130, currentImgX, currentImgY, 50, 50);
                // }
                break
        }
    }


    render() {
        return (
            <Fragment>
                <section id="lesson4">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 4</h2>
                                    <hr class="divider-title" />
                                </div>
                                <p class="china-font">RPG</p>
                                <p class="china-font">九宮格</p>
        Last key code pressed: <strong>{this.state.key}</strong>

                            </div>

                            <canvas class="canvas" ref="canvas"></canvas>
                            <br />

                            <div class="col-lg-8 mx-auto">
                                <button type="button" onClick={this.play}>上</button>
                                <button type="button" onClick={this.play}>下</button>
                                <button type="button" onClick={this.play}>左</button>
                                <button type="button" onClick={this.play}>右</button>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )
    }
}

export default Lesson4;
