import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson5 extends Component {

    constructor(props) {
        super(props);

        this.myRef = React.createRef();

        this.state = {
            player:'',
            currentPlay: 0,
            playList: [
                "4iVW7OhZ0p8",
                "MJnzEa0FSKk",
                "7NN4RlKspW0"
            ],
            playTime: [
                [57, 60],
                [79, 83],
                [76, 80]
            ]
        }
    }


    componentDidUpdate(e) {
        this.playVideo();
    }



    playVideo = () => {
        this.state.player = new YT.Player("player",
        {
            height:"390",
            width:"640"
        })
    }

    playVideoReady = () => {

    }

    playVideoChange = () => {

    }


    render() {
        return (
            <Fragment>
                <section id="lesson3">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 3</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">心理測試</p>
                                <p class="china-font">你的愛情是什麼滋味?</p>
                                <Controller data={this.state.playList} page={this.state.playTime} onClick={this.playVideo.bind(this)} />
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )
    }
}

export default Lesson5;
