import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson3 extends Component {


    state = {
        btnText: '開始',
        data: [
            {
                question: "你現在對今年非常樂觀?",
                ans: [["是的", 2], ["不是", 3], ["不知道", 4]]
            },
            {
                question: "你覺得自己是不是越來越不可靠?",
                ans: [["是的", 2], ["不是", 3], ["一般", 5]]
            },
            {
                question: "想到新的一年來到，就非常開心?",
                ans: [["是的", 4], ["不是", 5]]
            },
        ]
    }


    showNext = () => {
        this.setState({
            btnText:'下一個'
        })
        this.forceUpdate();

    }


    render() {
        return (
            <Fragment>
                <section id="lesson3">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 3</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">心理測試</p>
                                <div class="input-group mb-3">
                                    <div class="input-group-append">
                                        <button class="btn btn-outline-secondary" type="button" onClick={this.showNext}>{this.state.btnText}</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )

    }
}

export default Lesson3;
