import React from 'react';


export default ({ data, page, onClick, myRef, text, onchange ,myInput}) => {

    let list = [];

    let html_ = '';

    let title_ = '測試完成';

    if(page < data.length){
        for (let i = 0; i < data[page].answers.length; i++) {
            list.push(
                <li class="list-group-item">
                    <div class="form-check">
                        <input class="form-check-input" type="radio" name={data[page].question} id={data[page].question} value={data[page].answers[i][1]}  onChange={onchange} />
                        <label class="form-check-label" for={data[page].question}>
                            {data[page].answers[i][0]}
                        </label>
                    </div>
                </li>
            )
        }
        
        html_ = list;
        title_ = data[page].question;
    }


    return (
        <div>
            <h3>{title_}</h3>

            <ul class="list-group">
                {html_}
            </ul>
            <br/>
            <button class="btn btn-outline-secondary" type="button" onClick={onClick} ref={myRef} disabled>{text}</button>
        </div>
    )
};
