import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson4 extends Component {

    constructor(props) {
        super(props);

        this.talkBox = React.createRef();

        this.state = {
            imgMain: new Image(),
            mapArray: [0,1,1,0,0,0,3,1,2]
        }
    }

    componentDidUpdate(){
        this.talkBox.getContext('2d');
        
        this.state.imgMain.src = "../assets/spriteSheet.png";
        this.state.imgMain.onload = function(){
            talkBox.drawImage(imgMain,0,0,80,130,0,0);
        }

    }





    play = () => {
        console.log(this.talkBox.current);
    }


    render() {
        return (
            <Fragment>
                <section id="lesson4">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 4</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">RPG</p>
                                <p class="china-font">九宮格</p>
                                <br/>
                                <Controller talkBox={this.talkBox} imgMain={this.state.imgMain}/>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )
    }
}

export default Lesson4;
