import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson2 extends Component {


    state = {
        startDate: new Date(),
        dateList: [],
        title: ["尚未開學", "國定假日", "環境準備", "隨機性", "重複性", "條件判斷"],
        newDate:'2/21'
    };



    setMonAndDay = (startMon, startDay) => {
        // this.state.startDate.setMonth(startMon-1, startDay);
        // this.state.startDate.setHours(0);
        // this.state.startDate.setMinutes(0);
        // this.state.startDate.setSeconds(0);
        // this.dateList.push(this.state.startDate);

        // let date = new Date()
        this.state.startDate.setMonth(startMon - 1, startDay);
        this.state.startDate.setHours(0);
        this.state.startDate.setMinutes(0);
        this.state.startDate.setSeconds(0);

        // let startDate = new Date(date).toLocaleString('en-US',{hour12:false}).split(" ");
        // let time = startDate[1];
        // let mdy = startDate[0];
        // mdy = mdy.split('/');
        // let month = parseInt(mdy[0]);
        // let day = parseInt(mdy[1]);
        // let year = parseInt(mdy[2]);

        // let formattedDate = year + '/' + month + '/' + day ;
    }

    changeDate = () => {
        this.setState({neDate:'3/21'})
    }


    render() {
        return (
            <Fragment>
                <section id="lesson2">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 2</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">社團課程時間表</p>
                                <div class="input-group mb-3">
                                    <input type="text" value="" class="form-control" onChange={this.changeDate} placeholder="輸入日期格式 2/21" />
                                    <div class="input-group-append">
                                        <button class="btn btn-outline-secondary" type="button">輸入</button>
                                    </div>
                                </div>

                                <Controller startDate={this.state.startDate} title={this.state.title} />
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )

    }
}

export default Lesson2;
