import React from 'react';
import '../App.css';

export default ({talkBox, imgMain}) => {
    imgMain.src = "../assets/spriteSheet.png";
    return (
        <div>
            <canvas class="canvas"></canvas>
            <div class="talkBox" ref={talkBox}></div>
        </div>
    )
};
