import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson5 extends Component {

    constructor(props) {
        super(props);

        this.myRef = React.createRef();

        this.state = {
            player:'',
            currentPlay: 0,
            playList: [
                "4iVW7OhZ0p8",
                "MJnzEa0FSKk",
                "7NN4RlKspW0"
            ],
            playTime: [
                [57, 60],
                [79, 83],
                [76, 80]
            ]
        }
    }


    componentDidUpdate(e) {
        this.playVideo();
    }



    playVideo = () => {

        this.state.player = new YT.Player('player', {
            fitToBackground: true,
            height: '100%',
            width: '100%',
            videoId: 'vKSA_idPZkc', //Youtu ID
            playerVars: { //自訂參數
              'controls': 1, //控制列，0:隱藏，1:顯示(默認)
              'fs': 0, //全屏按鈕，0:隱藏，1:顯示(默認)
              'iv_load_policy': 3, //影片註釋，1:顯示(默認)，3:不顯示
              'rel': 0, //顯示相關視頻，0:不顯示，1:顯示(默認)
              'modestbranding': 1, //YouTube標籤，0:顯示(默認)，1:不顯示    
              'playsinline': 1 //在iOS的播放器中全屏播放，0:全屏(默認)，1:內嵌
            },
            events: {
              'onReady': yt.onPlayerReady, //當播放器準備就緒時執行
              'onStateChange': yt.onPlayerStateChange //當播放器狀態改變時執行
            }
          });
    }

    playVideoReady = () => {

    }

    playVideoChange = () => {

    }


    render() {
        return (
            <Fragment>
                <section id="lesson3">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 3</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">心理測試</p>
                                	
<iframe src="https://www.youtube.com/embed/PEr5agHOTcg" width="560" height="315" frameborder="0" allowfullscreen="allowfullscreen"></iframe>
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )
    }
}

export default Lesson5;
