import React, { Fragment, Component } from 'react';
import Controller from './Controller';

class Lesson3 extends Component {


    state = {
        page: 0,
        btnText: '開始',
        data: [
            {
                "question":"你現在對今年非常樂觀？",
                "answers":[
                    ["是的",2],
                    ["不是",3],
                    ["不知道",4]
                ]
            },
            {
                "question":"你覺得自己是不是越來越不可靠了？",
                "answers":[
                    ["是的",3],
                    ["不是",4],
                    ["一般",5]
                ]
            },
            {
                "question":"想到新的一年來到，就非常開心？",
                "answers":[
                    ["是的",4],
                    ["不是",5],
                    ["還好",6]
                ]
            },
            {
                "question":"如果給你五百萬要你離開很愛的戀人，你會？",
                "answers":[
                    ["好的",5],
                    ["都可以",6],
                    ["不要",7]
                ]
            },
            {
                "question":"過去一年裡，你有沒有受到很大的打擊？",
                "answers":[
                    ["有的",6],
                    ["沒有",7],
                    ["還在承受著呢",8]
                ]
            },
            {
                "question":"你喜歡下面哪一個形容天氣的詞？",
                "answers":[
                    ["風和日麗",7],
                    ["豔陽高照",8],
                    ["秋高氣爽","A"]
                ]
            },
            {
                "question":"你覺得自己的愛情一直是比較不一般的？",
                "answers":[
                    ["是的",8],
                    ["不是",9],
                    ["不知道",10]
                ]
            },
            {
                "question":"你會想要跟情人去哪裡旅行？",
                "answers":[
                    ["蔚藍海岸",9],
                    ["山林湖畔","A"],
                    ["歷史古鎮","B"]
                ]
            },
            {
                "question":"除了愛情，你更希望今年實現哪件事情？",
                "answers":[
                    ["買房子","C"],
                    ["工作升遷","D"],
                    ["變帥變美","A"]
                ]
            },
            {
                "question":"下面哪一種飲料中獎，你最想要？",
                "answers":[
                    ["優格再來一瓶","B"],
                    ["啤酒再來一瓶","C"],
                    ["紅茶再來一瓶","D"]
                ]
            }
        ],
        num: 0,
        count: 0,
        allSores: 0
    }


    showNext = () => {

        if(this.state.page > this.state.data)
        {
            this.setState({
                page: 0,
                btnText: '開始',
                num: 0,
                count: 0
            })
        }else{

            let number = this.state.page < this.state.data.length ? 1 + this.state.page : 4;
    
            let text_ = number < 3 ? '下一個' : '總分 : '+ this.state.count;
    
            this.setState({
                page: number,
                btnText: text_,
                count: parseInt(this.state.count) + parseInt(this.state.num)
            })
        }
    }

    getTotal = (e) => {
        this.setState({ num: e.target.value })
        if(this.state.page == 2){
            this.setState({count: parseInt(this.state.count) + parseInt(e.target.value) })
        }
    }


    render() {
        return (
            <Fragment>
                <section id="lesson3">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-8 mx-auto">
                                <div class="col-lg-5 text-center">
                                    <h2 class="textss">Lesson 3</h2>
                                    <hr class="divider-title" />
                                </div>

                                <p class="china-font">心理測試</p>
                                <p class="china-font">你的愛情是什麼滋味?</p>
                                <Controller data={this.state.data} page={this.state.page} onClick={this.showNext} text={this.state.btnText} count={this.state.count} onchange={this.getTotal.bind(this)} />
                            </div>
                        </div>
                    </div>
                </section>
            </Fragment >
        )

    }
}

export default Lesson3;
